class r{#e;#t;constructor(){this.#e=0,this.#t=null,this.initListeners()}refreshScreen=()=>{let e=String(Math.floor(this.#e/3600)).padStart(2,"0"),t=String(Math.floor(this.#e/60)).padStart(2,"0"),i=String(this.#e%60).padStart(2,"0");document.getElementById("timerEx6").textContent=`${e}:${t}:${i}`};startTimer=()=>{this.#t||(this.#t=setInterval(()=>{this.#e++,this.refreshScreen()},1e3))};pauseTimer=()=>{clearInterval(this.#t),this.#t=null};resetTimer=()=>{this.pauseTimer(),this.#e=0,this.refreshScreen()};initListeners=()=>{document.getElementById("btnInitTimer").addEventListener("click",()=>this.startTimer()),document.getElementById("btnPauseTimer").addEventListener("click",()=>this.pauseTimer()),document.getElementById("btnResetTimer").addEventListener("click",()=>this.resetTimer())}}const n=()=>{new r};export{n as initExercise};
